def add_one(number):
    """Add one to the given number.
    
    Args:
        number: The number to add one to.
        
    Returns:
        The number plus one.
    """
    return number + 1

def multiply_by_two(number):
    """Multiply the given number by two.
    
    Args:
        number: The number to multiply.
        
    Returns:
        The number multiplied by two.
    """
    return number * 2