from multicalc import calculator

calc = calculator.Calculator()

print("Add:       2 + 3 =", calc.add(2, 3))
print("Subtract:  5 - 1 =", calc.subtract(5, 1))
print("Multiply:  4 * 7 =", calc.multiply(4, 7))
print("Divide:    10 / 2 =", calc.divide(10, 2))
print("Power:     3 ** 4 =", calc.power(3, 4))